function uniqueCharacters($string, $length) {
	$unique = [];
	$charLength = strlen($string);
	$position = 0;
	for ($i = 0; $i < $charLength; $i++) {
		$charGroup = array_slice(str_split($string), $i, $length);
		if (count(array_unique($charGroup)) == $length) {
			return $i+$length;		 
		}
	}

  return false;
}


